/**
 * Gets a random set of questions from a specific category.
 * @param {string} categoryName - The name of the category (e.g., 'Shapes', 'Hindi').
 * @param {number} numQuestions - The number of random questions to return (e.g., 5).
 * @returns {Array} An array of unique, random questions, or an empty array if the category doesn't exist.
 */
export function getQuizQuestions(categoryName, numQuestions) {
  const categoryArray = quizData[categoryName];

  // Return an empty array if the category doesn't exist
  if (!categoryArray) {
    console.error(`Category "${categoryName}" not found.`);
    return [];
  }
  
  // Create a copy of the original array to avoid modifying it.
  const shuffled = [...categoryArray];
  
  // Shuffle the copied array using the Fisher-Yates algorithm.
  let currentIndex = shuffled.length;
  let randomIndex;

  // While there are elements left to shuffle...
  while (currentIndex !== 0) {
    // Pick a remaining element.
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // And swap it with the current element using modern array destructuring.
    [shuffled[currentIndex], shuffled[randomIndex]] = [
      shuffled[randomIndex], shuffled[currentIndex]];
  }
  
  // Return the first `numQuestions` items from the shuffled array.
  return shuffled.slice(0, numQuestions);
}
